import React, { useEffect, useState } from 'react';

const API = 'https://karljoke.herokuapp.com/jokes/random'

function App() {
  const [joke, setJoke] = useState('');
  const [showPunchLineButton, setShowPunchineButton] = useState(false);
  const [hidePunchLineButton, setHidePunchineButton] = useState(false);
  const showPunchLine = () => {
    fetch(API)
    .then(res => res.json())
    .then(joke => setJoke(joke.punchline))
    .catch(err => console.error(err));
    setHidePunchineButton(true);
  }
  useEffect(() => {
    showPunchLine();
  }, [])

  return (
    <div className="App">
      <button onClick={showPunchLine}>Show Punch Line</button>
      <button> Hide Punch Line</button>
      <p>{joke}</p>
    </div>
  );
}

export default App;
