import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';  
import {Nav, Navbar, Container, NavDropdown} from 'react-bootstrap';  

const API = 'https://karljoke.herokuapp.com/jokes/random'

function App() {
  const [joke, setJoke] = useState('');
  const [setup, setSetup] = useState('');
  const [showPunchLineButton, setShowPunchLineButton] = useState(true);
  const [hidePunchLineButton, setHidePunchLineButton] = useState(false);
  const showPunchLine = () => {
    fetch(API)
    .then(res => res.json())
    .then(joke => setJoke(joke.punchline))
    .catch(err => console.error(err));
    setHidePunchLineButton(true);
    setShowPunchLineButton(false);
  }

  const hidePunchLine = () => {
    setShowPunchLineButton(false);
    setJoke(false);
  }

  useEffect(() => {
    setTimeout(() => {
      fetch(API)
    .then(res => res.json())
    .then(data => setSetup(data.setup))
    .catch(err => console.error(err));
    }, 5000);
    setHidePunchLineButton(false);
  }, []);

  const generateRandomJoke = () => {
    fetch(API)
    .then(res => res.json())
    .then(data => setSetup(data.setup))
    .catch(err => console.error(err));
    setHidePunchLineButton(false);
    setShowPunchLineButton(true);
  }

  const viewApiDocs = () => {
    window.open("https://karljoke.herokuapp.com/");
  }

  return (
    <Navbar bg="primary" expand="md">  
    <Container>  
    <div className="App">
      <div className="header-details">
        <button onClick={generateRandomJoke}>Generate Random Joke</button>
        <button onClick={viewApiDocs}>View API Doc</button>
      </div>
      {setup ? '' : 
      <p> Loading your Joke </p>}
      <p>{setup}</p>
      {showPunchLineButton ? 
      <button onClick={showPunchLine}>Show Punch Line</button> : ''}
      {hidePunchLineButton ? 
      <button onClick={hidePunchLine}> Hide Punch Line</button> : ''}

      <p>{joke}</p> 
    </div>
      </Container>
      </Navbar>

  );
}

export default App;
